storecustom.scss:
/* CMS - Customer Service*/


div.top-fix {
    padding: 5px;
    border-top: 5px solid transparent;
    margin: -30px 0 0;
    background-clip: padding-box;
}
    
.border-text-container {
    border-left: 3px solid #f3c000;
    padding: 0px 1em;
}
.border-bottom.cs{padding-bottom: 2.5em !important}

.section-banner-img{
    position:relative;
    @media screen and (max-width: 1000px){
        background-image: url('https://cdn11.bigcommerce.com/s-ffckdl5hk2/product_images/uploaded_images/customer-sm-banner.png?t=1631068547'); position: relative; width: 100%; height: 10rem; background-size: cover; display: grid; place-content: center; border-radius: 5px; }
    @media screen and (max-width: 480px){height: 8rem; }  
    &>img{position:relative;width:100%;
        @media screen and (max-width: 1000px){
             display: none;}
        }
    .banner-text{position:absolute;top:50%;left:50%;transform:translate(-50%,-50%);
        @media screen and (max-width: 1000px){
            width: 85%; margin: 0 auto;}
    }
    .banner-text h1.page-heading{
        color:#fff!important;
        @media screen and (max-width: 1000px){
                text-align: center;}
                @media screen and (max-width: 480px){
                    font-size: 18px !important;}
        }
    }

div#menu-section-banner{
    &>ul{display:inline-flex;width:100%;list-style:none;margin-left:1em;margin-top:1em;
        @media screen and (max-width: 480px){
            display: block;
        }} 
    &>ul li::before{content:"\2022";color:#f3c000;font-weight:700;display:inline-block;width:1em;margin-left:-1em;}
    &>ul li{width:25%}
}

.section-text-content{
     border-top:1px solid #000;
     p{font-size:16px}
     .border-bottom{border-bottom:1px solid #000;padding-bottom:1em}
     h2, h4{ font-family: 'Anton' !important;  font-weight: 400 !important;}
     strong, b{font-weight: 800 !important;}
}


/* CMS - About Richmond Supply*/
.section-about-banner{
    position: relative;
    img{position: relative;}
    .banner-text.about{left:35%;position:absolute;top:50%;transform:translate(-50%,-50%);
    h1{color: #fff !important;
        @media screen and (max-width: 480px){font-size: 18px;}}
    }
}


.section-richmond-images{
    margin-bottom: 1.5em;
    .richmond-company-images { display: flex; width: 100%; justify-content: space-between; flex-flow: row wrap;
        div { max-width: 49%; }
    }
}

.section-contact-us{
    background-image:url("https://cdn11.bigcommerce.com/s-ffckdl5hk2/product_images/uploaded_images/email-banner-img.png?t=1631067611");width:100%;height:100%;padding:4.5rem 2rem;border-radius:5px;background-size:contain;background-repeat:no-repeat;
    @media screen and (max-width: 812px) {background-size: cover; }

    .richmond-company-images{
        display:flex;width:100%;justify-content:space-between;flex-flow:row wrap;align-items: center;
        @media screen and (max-width: 480px) {flex-direction: column-reverse;}
    }
    .richmond-company-images>div{max-width:49%;
        @media screen and (max-width: 480px) { max-width: 100%;}
    }
    .contact-us-content{
        display:flex;align-items:center;
        h6{
            font-weight:800!important;font-size:16px!important;color:#fff!important;
            @media screen and (max-width: 1200px) {
                br {  display: none;  }   
            }
        }
        p{font-size:16px;color:#fff;font-weight:800!important}
        div>a{background:#f3c000;color:#000!important;padding:1rem 4rem;border-radius:5px;text-decoration:none;text-transform:uppercase;font-weight:800;font-size:16px}
    }
}

/* CMS - Industrial Services We Offer*/
.cs-h4{margin-top: 2em !important;}
p.margin-bottom { margin-bottom: 0; }
.bold-color{color:#544d38;font-weight: 600;}
.bold-color.ul{color:#544d38;font-weight: 600;}

.menu-section-industrial>ul { column-count: 4; list-style: none; margin: 1em 0 0 1em; padding-bottom: 1em;
        li::before{content: "\25A0";color:#f3c000;font-weight:700;display:inline-block;width:1em;margin-left:-1em}
}
.menu-section-industrial-1>ul { column-count: 3; list-style: none; margin: 1em 0 0 1em; padding-bottom: 1em;
        li::before{content: "\25A0";color:#f3c000;font-weight:700;display:inline-block;width:1em;margin-left:-1em}
}

.red-text{ color: red !important; text-transform: capitalize; }


.content-flex-side { display: flex; width: 100%; justify-content: space-between; padding: 1rem 0; border-bottom: 1px solid black; align-items: center;
    @media screen and (max-width: 480px) {
        flex-direction:column-reverse;
    }

}
.content-flex-side>div:first-child{
    width: 68%;
    @media screen and (max-width: 800px) {
        width: 47%;
    }
    @media screen and (max-width: 480px) {
        width: 100%;
    }
}
.content-flex-side div:last-child{
    width: 30%;
    @media screen and (max-width: 800px) {
        width: 47%;
    }
    @media screen and (max-width: 480px) {
        width: 100%;
    }
}

/* CMS - National Affiliations*/

.banner-text.about.affiliations { display: flex; justify-content: space-between; align-items: center; width: 100%; left: 56%;
    
    @media screen and (max-width: 1023px) { 
        left: 50%; justify-content: center; }
    @media all and (min-width: 667px) and (max-width: 1023px) {
        padding: 0 12px;}


    div {
        width: 50%;
        @media screen and (max-width: 1023px) { justify-content: center; display: flex;  }
        @media screen and (max-width: 667px) { width: 47%; }

        h1{
            @media screen and (max-width: 1080px) {
             font-size: 22px !important;
            }
            @media screen and (max-width: 667px) {
                font-size: 16px !important;
            }
                strong>br {
                    @media screen and (max-width: 837px) {
                        display: none;
                    }
                }
        }
    }
}

@media screen and (max-width: 800px) {
    .section-about-banner.affiliation { position: relative; background-image: url("https://cdn11.bigcommerce.com/s-ffckdl5hk2/product_images/uploaded_images/mro-affiliations.png?t=1631068262&_gl=1*2o0st4*_ga*MTgzNTE5MzI1My4xNjE3Nzc3MjAy*_ga_WS2VZYPC6G*MTYzMTA2NTI3My4xNjQuMS4xNjMxMDY4MjM1LjU5"); height: auto; width: 100%; padding: 5rem 0rem; border-radius: 5px; background-size: cover;
        img.ban-img { display: none; }
    }
}
    .video-container {  position: relative; padding-bottom: 56.25%; /* 16:9 */ height: 0;
        iframe { position: absolute; top: 0; left: 0; width: 100%; height: 100%; }
    }
