components/common/social-links:
{{#if social_media}}
    <h5 class="footer-info-heading">{{lang 'social.connect'}}</h5>
    <ul class="socialLinks socialLinks--alt">
        {{#each social_media}}
            <li class="socialLinks-item">
                {{#if display_name '===' 'google+'}}
                    <a class="icon icon--google" href="{{url}}" target="_blank">
                        <svg><use xlink:href="#icon-google" /></svg>
                    </a>
                {{else}}
                    <a class="icon icon--{{display_name}}" href="{{url}}" target="_blank">
                        <svg><use xlink:href="#icon-{{display_name}}" /></svg>
                    </a>
                {{/if}}
            </li>
        {{/each}}
    </ul>
{{/if}}

components/custom/instagram-feed:
{{#if theme_settings.enable_instagram_feed '===' true}}
<section class="content">
    <h2 class="container-header instagram-header"><span>{{lang 'social-feed.latest_instagram'}}</span></h2>
    <div id="instafeed" class="instagramFeed postColumns--{{theme_settings.instagram_columns_count}}"></div>
</section>
{{/if}}
