                <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">
                <meta itemprop="valueAddedTaxIncluded" content="false">
            </div>
        {{/if}}
    </div>
{{/and}}

{{!-- Never display the "saving price" element by default if there is a price range on the page to be shown --}}
{{#if page_type '===' 'product'}}
     <div class="price-section price-section--saving price" style="display: none;">
            <span class="price">{{lang 'products.you_save_opening_text'}}</span>
                 <span data-product-price-saved class="price price--saving">{{price.saved.formatted}}</span>
                 <span class="price">{{lang 'products.you_save_closing_bracket'}}</span>
     </div>
{{/if}}

components/products/ratings:
{{#for 1 5}}
    {{#if ../this.rating '>=' $index}}
        <span class="icon icon--ratingFull">
            <svg>
                <use xlink:href="#icon-star" />
            </svg>
        </span>
    {{else}}
        <span class="icon icon--ratingEmpty">
            <svg>
                <use xlink:href="#icon-star" />
            </svg>
        </span>
    {{/if}}
{{/for}}
{{{snippet 'product_rating'}}}

pages/home:
---
products:
    new:
        limit: {{theme_settings.homepage_new_products_count}}
    featured:
        limit: {{theme_settings.homepage_featured_products_count}}
    top_sellers:
        limit: {{theme_settings.homepage_top_products_count}}
carousel: {{theme_settings.homepage_show_carousel}}
blog:
    recent_posts:
        limit: {{theme_settings.homepage_blog_posts_count}}
---
{{#partial "hero"}}

{{/partial}}

{{#partial "page"}}

<div class="clear"></div>

<div class="home{{#if theme_settings.home_page_with_sidenav '!==' false}} home-page-with-sidenav{{/if}} {{#if theme_settings.home_page_with_sidenav '===' false}} home-page-no-sidenav {{/if}} {{#if theme_settings.homepage_show_carousel '!==' true}} no-hero-carousel{{/if}} {{#if theme_settings.slider_width '!==' 'full-width-slider'}} slider-in-container{{/if}}">

    {{#if ../theme_settings.home_page_with_sidenav '===' false}}
      {{> components/page/home-page-no-sidenav}}
    {{/if}}

    <!-- SHOW SIDENAV BELOW CAROUSEL -->
    {{#if ../theme_settings.home_page_with_sidenav '===' true}}
    {{#if theme_settings.slider_width '!==' 'in-container-floated'}}
        {{> components/page/home-page-with-sidenav}}
      {{/if}}
    {{/if}}

    <!-- SHOW SIDENAV NEXT TO CAROUSEL -->
    {{#if ../theme_settings.home_page_with_sidenav '===' true}}
    {{#if theme_settings.slider_width '===' 'in-container-floated'}}
        {{> components/page/home-page-with-sidenav-float-carousel}}
      {{/if}}
    {{/if}}

</div>

{{/partial}}
{{> layout/home-layout}}
